import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [mood, setMood] = useState('');
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchSongs = async () => {
    if (!mood) return;
    setLoading(true);
    setError('');
    try {
      const res = await axios.get(`http://localhost:5000/music/${mood}`);
      setSongs(res.data);
    } catch (err) {
      setError('Failed to load songs. Is the backend running?');
      console.error(err.message);
    }
    setLoading(false);
  };

  return (
    <div className="App">
      <h1>MoodMixer ðŸŽ§</h1>
      <input
        type="text"
        placeholder="Enter your mood (e.g. happy, chill)"
        value={mood}
        onChange={(e) => setMood(e.target.value)}
      />
      <button onClick={fetchSongs}>Get Music</button>

      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}

      <ul className="song-list">
        {songs.map((song, index) => (
          <li key={index}>
            <a href={song.url} target="_blank" rel="noreferrer">
              {song.name} â€” {song.artist}
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
